#include <unistd.h>
#include <stdio.h>
#include <string.h>
#include <fcntl.h>
#include <errno.h>
#include <stdlib.h>

extern int errno;

ssize_t	ft_strlen(const char *str);
ssize_t ft_write(int fd, const void *buf, size_t n);
ssize_t ft_read(int fd, const void *buf, size_t n);
int		ft_strcmp(const char *s1, const char *s2);
char	*ft_strdup(const char *s1);
char	*ft_strcpy(char * dst, const char * src);

void	ft_read_check(char *fname, int fd)
{
	char	buff[255];
	
	errno = 0;
	if (fd > 2)
		fd = open(fname, O_RDONLY);
	printf("\nft_read:	%ld|", ft_read(fd, buff, 255));
	printf("error: %s\n", strerror(errno));
	errno = 0;
	if (fd != 0)
		close(fd);
	if (fd > 2)
		fd = open(fname, O_RDONLY);
	printf("   read:	%ld|", read(fd, buff, 255));
	printf("error: %s\n", strerror(errno));
	printf("__________________________________________\n");
	close(fd);
}

void	ft_write_check(char *str, int fd)
{
	int	tmp;

	errno = 0;
	write(fd, "\nft_write:	", 10);
	tmp = ft_write(fd, str, strlen(str));
	printf("\nft_write:	%d|", tmp);
	printf("error: %s\n", strerror(errno));
	write(fd, "   write:	", 11);
	tmp = write(fd, str, strlen(str));
	printf("\n   write:	%d|", tmp);
	printf("error: %s\n", strerror(errno));
	printf("__________________________________________\n");
}

void	ft_strcmp_check(char *str1, char *str2)
{
	printf("\nft_strcmp:	%d\n", ft_strcmp(str1, str2));
	printf("   strcmp:	%d\n", strcmp(str1, str2));
	printf("__________________________________________\n");
}

void	ft_strcpy_check(char *s)
{
	char	*str;
	str = malloc(strlen(s));
	ft_strcpy(str, s);
	printf("\nft_strcpy:	%s\n", str);
	strcpy(str, s);
	printf("   strcpy:	%s\n", str);
	printf("__________________________________________\n");
}

void	ft_strdup_check(char *s)
{
	char *str;

	str = ft_strdup(s);
	printf("\nft_strdup:	%s\n", str);
	free(str);
	str = NULL;
	str = strdup(s);
	printf("   strdup:	%s\n", str);
	printf("__________________________________________\n");
	free(str);
}

void	ft_strlen_check(char *str)
{
	printf("\nft_strlen:	%d\n", (int)ft_strlen(str));
	printf("   strlen:	%d\n", (int)strlen(str));
	printf("__________________________________________\n");

}

int main(void)
{
	char	buff[255];

	/***************************/
	/********ft_read************/
	/***************************/
	printf("_______ \n       |\nFT_READ|\n_______|__________________________________\n");
	ft_read_check("ft_read.s", 3);
	ft_read_check("test", -1);
	ft_read_check("test", 0);
	read(1, buff, 1);
	/***************************/
	/********ft_write***********/
	/***************************/
	printf("________ \n        |\nFT_WRITE|\n________|__________________________________\n");
	ft_write_check("test", 1);
	ft_write_check("test", -1);
	read(1, buff, 1);
	/***************************/
	/********ft_strcmp**********/
	/***************************/
	printf("_________ \n         |\nFT_STRCMP|\n_________|__________________________________\n");
	ft_strcmp_check("", "");
	ft_strcmp_check("", "asd");
	ft_strcmp_check("asd", "");
	ft_strcmp_check("hello123", "world");
	ft_strcmp_check("hello", "hello123");
	ft_strcmp_check("hello", "hello—Ñ123");
	read(1, buff, 1);
	/***************************/
	/********ft_strcpy**********/
	/***************************/
	printf("_________ \n         |\nFT_STRCPY|\n_________|__________________________________\n");
	ft_strcpy_check("");
	ft_strcpy_check("");
	read(1, buff, 1);
	/***************************/
	/********ft_strdup**********/
	/***************************/
	printf("_________ \n         |\nFT_STRDUP|\n_________|__________________________________\n");
	ft_strdup_check("");
	ft_strdup_check("");
	read(1, buff, 1);
	/***************************/
	/********ft_strlen**********/
	/***************************/
	printf("_________ \n         |\nFT_STRLEN|\n_________|__________________________________\n");
	ft_strlen_check("");
	ft_strlen_check("q—Ü—ã");
	ft_strlen_check("");
	return (0);
}
